import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity} from 'react-native';
import { useState,useEffect } from 'react';
import {getMembersCount}  from './functions.js'

export default function App() {
  const [membersCount, setMembersCount] = useState(0);
  useEffect(async() => {
    const getMembersCount =async()=> {
      try {
        const response = await fetch(
          'http://localhost:3232/api/members'
        );
        const json = await response.json();
        const membersCounter=JSON.stringify(json);
        return membersCounter;
      } catch (error) {
        console.error(error);
      }
    };
    setMembersCount(await getMembersCount());
  });
  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.searchButton} onPress={findСompanion}>
        <Text style={styles.searchButtonText}>Найти собеседника</Text>
      </TouchableOpacity>
      <Text style={styles.membersText}>Число участников: {membersCount}</Text>
    </View>
  );
}

const findСompanion = () => {
  getMembersCount();
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  searchButtonText:{
    fontSize: '27%',
    fontWeight: '400',
    color: '#dddded',
    padding: 10,
  },
  membersText:{
    color:'#dddded',
    fontSize: '20%',
  },
  searchButton: {
    width:'70%',
    height: '10%',
    backgroundColor: '#3a3de0',
    borderRadius: 10,
    alignItems: 'center',
    justifyContent: 'center',
    marginVertical:'65%',
 },
});
