import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity,} from 'react-native';
import { getMembersCount } from './functions';


export default function App() {
  let members=0;
  getFeatures().then(x => {
    members=x;
});
  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.searchButton} onPress={findСompanion}>
        <Text style={styles.searchButtonText}>Найти собеседника</Text>
      </TouchableOpacity>
      <Text style={styles.membersText}>Число участников: {members}</Text>
    </View>
  );
}

const findСompanion = () => {
  getMembersCount()
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  searchButtonText:{
    fontSize: '27%',
    fontWeight: '400',
    color: '#dddded',
    padding: 10,
  },
  membersText:{
    color:'#dddded',
    fontSize: '20%',
  },
  searchButton: {
    width:'70%',
    height: '10%',
    backgroundColor: '#3a3de0',
    borderRadius: 10,
    alignItems: 'center',
    justifyContent: 'center',
    marginVertical:'65%',
 },
});
