import React, { useRef } from 'react';
import { StyleSheet, Text,TextInput, View, TouchableOpacity} from 'react-native';
import { useState,useEffect} from 'react';

let ws=new WebSocket("ws://localhost:2323");

const [companionNickName, setCompanionNick] = useState<String>('');

export default function App() {
  const [membersCount, setMembersCount] = useState(0);
  useEffect(() => {
    const getMembersCount =async()=> {
      try {
        const response = await fetch(
          'http://localhost:3232/api/members',{
            method: 'Get',
            //headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
              'Access-Control-Allow-Origin' : '*',
            //},
          });
        const json = await response.json();
        const membersCount=JSON.stringify(json);
        setMembersCount(membersCount);
      } catch (error) {
        console.error(error);
      }
    };
    getMembersCount();
  });
  return (
    <View style={styles.container}>
      <View style={styles.footer}>
        <Text styile={styles.footerText}>Никнейм:</Text>
        <TextInput style={styles.textInput}/>
      </View>
      <TouchableOpacity style={styles.searchButton} onPress={findСompanion}>
        <Text style={styles.searchButtonText}>Найти собеседника</Text>
      </TouchableOpacity>
      <Text style={styles.membersText}>Число участников: {membersCount}</Text>
    </View>
  );
}

const findСompanion = () => {
  useEffect(() => {
    const getCompanion =async()=> {
      try {
        const response = await fetch(
          'http://localhost:3232/api/getCompanion',{
            method: 'Get',
            //headers: {
              Accept: 'application/json',
              'Content-Type': 'application/json',
              'Access-Control-Allow-Origin' : '*',
            //},
          });
        const json = await response.json();
        const companionNickName=JSON.stringify(json);
        setCompanionNick(companionNickName);
      } catch (error) {
        console.error(error);
      }
    };
    getCompanion();
  });
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  footer:{
    alignItems:'center',
    justifyContent:'center',
  },
  footerText:{
    color:'#dddded',
    fontSize: '20%',
  },
  textInput:{
    backgroundColor:'#dddded',
    width:'70%',
    height: '20%',
  },
  searchButtonText:{
    fontSize: '27%',
    fontWeight: '400',
    color: '#dddded',
    padding: 10,
  },
  membersText:{
    color:'#dddded',
    fontSize: '20%',
  },
  searchButton: {
    width:'70%',
    height: '10%',
    backgroundColor: '#3a3de0',
    borderRadius: 10,
    alignItems: 'center',
    justifyContent: 'center',
 },
});
